import { useEffect, useState } from "react"
import useSound from "use-sound"
import { AiFillPlayCircle, AiFillPauseCircle } from "react-icons/ai"
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi"
import { IconContext } from "react-icons"

const MusicPlayer = () => {
	const [isPlaying, setIsPlaying] = useState(false)
	const [seconds, setSeconds] = useState() // current position of the audio in seconds

	const apiUrl = "http://localhost:8080/api/music/stream" // Your backend API endpoint

	const [play, { pause, duration, sound }] = useSound(apiUrl, {
		format: "mp3", // Specify the audio format if needed
	})


	const [time, setTime] = useState({
		min: "",
		sec: "",
	})
	const [currTime, setCurrTime] = useState({
		min: "",
		sec: "",
	})

	useEffect(() => {
		if (duration) {
			const sec = duration / 1000
			const min = Math.floor(sec / 60)
			const secRemain = Math.floor(sec % 60)
			setTime({
				min: min,
				sec: secRemain,
			})
		}
	}, [isPlaying])

	useEffect(() => {
		const interval = setInterval(() => {
			if (sound) {
				setSeconds(sound.seek([]))
				const min = Math.floor(sound.seek([]) / 60)
				const sec = Math.floor(sound.seek([]) % 60)
				setCurrTime({
					min,
					sec,
				})
			}
		}, 1000)
		return () => clearInterval(interval)
	}, [sound])

	const playingButton = () => {
		if (isPlaying) {
			pause()
			setIsPlaying(false)
		} else {
			play()
			setIsPlaying(true)
		}
	}

	return (
		<div className="component">
			<h2>Playing Now</h2>
			<img
				className="musicCover"
				src="https://picsum.photos/200/200"
				alt="Album cover"
			/>
			<div>
				<h3 className="title">Music</h3>
				<p className="subTitle">Song</p>
			</div>
			<div>
				<div className="time">
					<p>
						{currTime.min}:{currTime.sec}
					</p>
					<p>
						{time.min}:{time.sec}
					</p>
				</div>
				<input
					type="range"
					min="0"
					max={duration / 1000}
					default="0"
					value={seconds}
					className="timeline"
					onChange={(e) => {
						sound.seek([e.target.value])
					}}
				/>
			</div>
			<div>
				<button className="playButton">
					<IconContext.Provider
						value={{ size: "3em", color: "#27AE60" }}
					>
						<BiSkipPrevious />
					</IconContext.Provider>
				</button>
				{!isPlaying ? (
					<button className="playButton" onClick={playingButton}>
						<IconContext.Provider
							value={{ size: "3em", color: "#27AE60" }}
						>
							<AiFillPlayCircle />
						</IconContext.Provider>
					</button>
				) : (
					<button className="playButton" onClick={playingButton}>
						<IconContext.Provider
							value={{ size: "3em", color: "#27AE60" }}
						>
							<AiFillPauseCircle />
						</IconContext.Provider>
					</button>
				)}
				<button className="playButton">
					<IconContext.Provider
						value={{ size: "3em", color: "#27AE60" }}
					>
						<BiSkipNext />
					</IconContext.Provider>
				</button>
			</div>
		</div>
	)
}

export default MusicPlayer
