import React, { useState, useRef, useEffect } from "react"
import ReactPlayer from "react-player"
import { FaPlay, FaPause } from "react-icons/fa"

const MusicPlayer = () => {
	const [playing, setPlaying] = useState(false)
	const [played, setPlayed] = useState(0)
	const [volume, setVolume] = useState(0.5)
	const [duration, setDuration] = useState(0)
	const [currentTime, setCurrentTime] = useState(0)
	const [seeking, setSeeking] = useState(false)

	const playerRef = useRef(null)

	const handlePlayPause = () => {
		setPlaying((prevPlaying) => !prevPlaying)
	}

	const handleProgress = (progress) => {
		if (!seeking) {
			setPlayed(progress.played)
			setCurrentTime(progress.played * duration)
		}
	}

	const handleSeek = (e) => {
		const seekTime = parseFloat(e.target.value)
		setPlayed(seekTime)
		setCurrentTime(seekTime * duration)
		playerRef.current.seekTo(seekTime, "fraction")
	}

	const handleSeekMouseDown = () => {
		setSeeking(true)
	}

	const handleSeekMouseUp = () => {
		setSeeking(false)
	}

	const handleVolumeChange = (e) => {
		const newVolume = parseFloat(e.target.value)
		setVolume(newVolume)
	}

	const handleDuration = (duration) => {
		setDuration(duration)
	}

	const handleEnded = () => {
		setPlaying(false) // or move on to the next track
	}

	useEffect(() => {
		console.log("MusicPlayer component re-rendered")
	}, [])

	const apiUrl = "http://localhost:8080/api/music/stream"

	return (
		<div>
			<h2>Music Player</h2>
			<ReactPlayer
				ref={playerRef}
				url={apiUrl}
				playing={playing}
				volume={volume}
				onPlay={handlePlayPause}
				onPause={handlePlayPause}
				onProgress={handleProgress}
				onDuration={handleDuration}
				onSeek={handleSeek}
				onEnded={handleEnded}
			/>
			<div>
				<button onClick={handlePlayPause}>
					{playing ? <FaPause /> : <FaPlay />}
				</button>
				<input
					type="range"
					min={0}
					max={1}
					step="any"
					value={played}
					onChange={handleSeek}
					onMouseDown={handleSeekMouseDown}
					onMouseUp={handleSeekMouseUp}
				/>
			</div>
			<div>
				<input
					type="range"
					min={0}
					max={1}
					step="any"
					value={volume}
					onChange={handleVolumeChange}
				/>
			</div>
			<div style={{ marginTop: "10px" }}>
				<strong>Status: {playing ? "Playing" : "Paused"}</strong>
				<p>Duration: {Math.floor(duration)}</p>
				<p>Current Time: {Math.floor(currentTime)}</p>
			</div>
		</div>
	)
}

export default MusicPlayer
